  test("Compact smaller query"){
    val query = "CREATE (TheMatrix:Movie {title:'The Matrix', released:2001, tagline:'Welcome to the Real World3'})\nCREATE (Keanu:Person {name:'Keanu Reeves', born:1964})\nCREATE (Carrie:Person {name:'Carrie-Anne Moss', born:1967})\nCREATE (Laurence:Person {name:'Laurence Fishburne', born:1961})\nCREATE (Hugo:Person {name:'Hugo Weaving', born:1960})\nCREATE (AndyW:Person {name:'Andy Wachowski', born:1967})\nCREATE (LanaW:Person {name:'Lana Wachowski', born:1965})\nCREATE (JoelS:Person {name:'Joel Silver', born:1952})\nCREATE\n  (Keanu)-[:ACTED_IN {roles:['Neo']}]->(TheMatrix),\n  (Carrie)-[:ACTED_IN {roles:['Trinity']}]->(TheMatrix),\n  (Laurence)-[:ACTED_IN {roles:['Morpheus']}]->(TheMatrix),\n  (Hugo)-[:ACTED_IN {roles:['Agent Smith']}]->(TheMatrix),\n  (AndyW)-[:DIRECTED]->(TheMatrix),\n  (LanaW)-[:DIRECTED]->(TheMatrix),\n  (JoelS)-[:PRODUCED]->(TheMatrix)\n\nRETURN TheMatrix\n"
    val result = executeWithCostPlannerOnly(query)
    println(result.executionPlanDescription())
    assertStats(result, nodesCreated = 8, relationshipsCreated = 7, propertiesWritten = 21, labelsAdded = 8)
  }

  test("Don't compact complex query") {
    val query = "EXPLAIN LOAD CSV WITH HEADERS FROM {csv_filename} AS line MERGE (u1:User {login: line.user1}) MERGE " +
      "(u2:User {login: line.user2}) CREATE (u1)-[:FRIEND]->(u2)"
    val result = executeWithCostPlannerOnly(query)
    println(result.executionPlanDescription())
  }

  test("Don't compact this query") {
    val a = createLabeledNode(Map("name"->"Keanu Reeves"), "Actor")
    val b = createLabeledNode(Map("name"->"Craig"), "Actor")
    val c = createLabeledNode(Map("name"->"Olivia"), "Actor")
    val d = createLabeledNode(Map("name"->"Carrie"), "Actor")
    val e = createLabeledNode(Map("name"->"Andres"), "Actor")
    relate(a,b)
    relate(b,c)
    relate(c,d)
    relate(d,e)
    relate(c,b)
    relate(d,b)
    val query = "PROFILE MATCH (n:Actor {name:'Keanu Reeves'})-->()-->(b) RETURN b"
    val result = executeWithCostPlannerOnly(query)
    println(result.executionPlanDescription())
  }
